import React,{useState, useEffect} from 'react';
import { Progress } from 'react-sweet-progress';
import "react-sweet-progress/lib/style.css";
import axios from 'axios';
import {Form} from 'react-bootstrap';


export default function PerbandinganBarang() {

	const [banding, setBanding] = useState([]);
	const [banding2, setBanding2] = useState([]);
	const [tanggal1, setTanggal1] = useState(null);
	const [tanggal2, setTanggal2] = useState(null);

	useEffect(() => {
		let isLoading = true;
		const tampil = async () => {
		const pangs = await axios.get("http://localhost:3001/api/perbandinganDataTerjual")
		.then((res) => {
			if(isLoading){
				setBanding(res.data);	
			}
		})
	}
	tampil();
	return () => {isLoading = false};
	}, [])

	useEffect(() => {
		let isLoading  = true;
		const tampilkan = async () => {
		const panggil = await axios.get(`http://localhost:3001/api/perbandinganDataTerjual2/${tanggal1}&${tanggal2}`)
		.then((res) => {
			if(isLoading){
				setBanding2(res.data);	
			}
		})
	}
	tampilkan();
	return () => { isLoading = false};
	})

	return (

		<>
		<h1>Testing Perbandingan Barang</h1>
		{banding.map((s,i) => {
			const persen = parseFloat((s.jumlah_jenis/s.total_terjual)*100).toFixed(2);
			return(
		<p>
		<h1>{s.barang}</h1>
		<Progress
		  type="circle"
		  strokeWidth={8}
		  percent={persen}
		/>
		<b>Terbesar : {s.terbesar}</b>&nbsp;&nbsp;
		<b>Terkecil : {s.terkecil}</b>
		</p>
		)
		})}
		
		<br />
		<p>
	{/* filter by tanggal yang dipilih*/}
		<h1> Filter By Date</h1>
		<p>Urutkan Berdasarkan Transaksi dari tanggal <Form.Control type="date" onChange={(e) => {setTanggal1(e.target.value)}}/></p>
		<p>ke Tanggal <Form.Control type="date" onChange={(e) => {setTanggal2(e.target.value)}}></Form.Control></p>
		</p>
		{banding2.map((s,i) => {
			const persen = parseFloat((s.jumlah_jenis/s.total_terjual)*100).toFixed(2);
			return(
		<p>
		<h1>{s.barang}</h1>
		<Progress
		  type="circle"
		  strokeWidth={8}
		  percent={persen}
		/>
		<b>Terbesar : {s.terbesar}</b>&nbsp;&nbsp;
		<b>Terkecil : {s.terkecil}</b>

		</p>
		)
		})}
		</>
		)
}
