import React from 'react';
import {Container, Card, Row, Col, CardGroup, Image} from 'react-bootstrap';
import Depan from '../component/utama.jpg';
import Bounce from 'react-reveal/Bounce';
import Pencarian from '../component/PencarianBarang';
import TampilPenjualan from '../component/TampilPenjualan';
import Fade from 'react-reveal/Fade';
import Zoom from 'react-reveal/Zoom';

export default function HalamanUtama(){
	return(

		<>
		<Card  className="bg-white text-white">
		<Zoom>
		<Card.Img src={Depan} alt="Card image" />
		</Zoom>
  		<Card.ImgOverlay>
    	 <center>
		  <Fade top>
		    <h1>Selamat Datang Di Website Penjualan Kami</h1>
		  <Card.Text>By ADRIAN SYAHPUTRA ALFINDA</Card.Text>
		  </Fade>
		  </center>
		</Card.ImgOverlay>
		</Card>
		<Container fluid>
		  <Row>
		  <Fade bottom>
		  <div style={{padding: '80px', width: '100%', height: '50%' , backgroundImage: `url(${Depan})`, backgroundSize: 'cover', backgroundAttachment: 'fixed'}}>
		    <center>
		    <p style={{color: 'white', fontSize: '50px', textDecoration: 'bold'}}><b>ANTAPANI</b></p>
		    </center>
		    </div>
		  	<Pencarian />
		  	<TampilPenjualan />
		  </Fade>
		  </Row>	
		</Container>

		</>


		);
}
