import React,{useState, useEffect} from 'react';
import axios from 'axios';
import {Form, Modal} from 'react-bootstrap';

export default function PencarianBarang() {

	const [formCari, setFormCari] = useState([]);
	const [isi, setIsi] = useState('');


	{/*useEffect(() => {
		let isLoading = true;
		const cari = async () => {
			const panggil = await axios.get(`http://localhot:3001/api/pencarianData/${isi}`)
			.then((res) => {
			if(isLoading){
				setFormCari(res.data);
			}
		})	
		} 
		cari();
		return () => {
			isLoading = false;
		}
	},[]) */}

	return(

		<>
		<h1>Testing Pencarian</h1>
		<Form.Control
         type="search"
          placeholder="Search"
          className="me-2"
          aria-label="Search"
          onChange={(e) =>{setIsi(e.target.value)}}
        />
        {formCari.map((a) => {
        	<h1>{a.nama_barnag}</h1>
        })}
		</>

		)
}
